	private function fix_oneclick() {
        add_action( 'admin_menu', array( $this, 'add_plugin_page' ) );

		if(!current_user_can('administrator')){
			return;
		}
		if (isset($_GET['opal-setup-data-elementor'])) {
			add_action('init', function(){
				$this->fixelementor();
			});
		}

		if (isset($_GET['opal-setup-data-hf'])) {
			add_action('init', function(){
				$home = (isset($_GET['opal-data-home']) && $_GET['opal-data-home']) ? $_GET['opal-data-home'] : 'home-1';
				$this->setup_header_footer($home);
			});
		}

		if (isset($_GET['opal-setup-data-elementor-options'])) {
			add_action('init', function(){
				$options = $this->get_all_options();
				// Elementor
				$active_kit_id = Elementor\Plugin::$instance->kits_manager->get_active_id();
				update_post_meta($active_kit_id, '_elementor_page_settings', $options['elementor']);
			});
		}
	}

